mapper.select:
select c.id, c.req_id as reqId, c.module_id as moduleId, m.module_name as moduleName, c.status, cs.status_name as statusName, r.summary as reqDesc, c.manpower, c.spliter, u1.user_name as spliterName, c.attachment, date_format(c.split_date, '%Y-%m-%d') as splitDate, c.responser, u2.user_name as responserName, date_format(c.plan_begin, '%Y-%m-%d') as planBegin, date_format(c.plan_to, '%Y-%m-%d') as planTo, c.summary, c.detail from sepp_code_mission c, sepp_requirement r , sepp_user u1, sepp_user u2, sepp_cm_status cs, sepp_prod_module m where r.product_id = #{productId} and c.req_id = r.id and c.spliter = u1.user_id and c.responser = u2.user_id and cs.status_id = c.status and m.module_id = c.module_id
and c.id = #{id}
and r.rel_id = #{relId}
and c.req_id = #{reqId}
and c.spliter = #{spliter}
and c.module_id = #{moduleId}
and c.status in
#{status}
and c.responser = #{responser}
and c.split_date >= #{splitDateBegin}
and c.split_date <= #{splitDateEnd}
and c.plan_begin >= #{planBeginBegin}
and c.plan_begin <= #{planBeginEnd}
and c.plan_to >= #{planToBegin}
and c.plan_to <= #{planToEnd}
order by c.id desc

mapper.update:
update
    sepp_code_mission
set
    status=#{status}
where
    id=#{id}

update
    sepp_code_mission
set
    status=#{status}
where
    req_id=#{reqId}
